PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE records (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  type TEXT NOT NULL CHECK(type IN ('支出', '收入')),
  category TEXT NOT NULL CHECK(category IN (
    '生活费', '交通', '饮食', '日用品', '娱乐', '学习', 
    '电子产品', '人情', '宠物', '饰品', '美妆护肤', '医疗', 
    '通讯', '服饰', '还贷',
    '工资', '投资收入', '稿费收入', '其他'
  )),
  amount REAL NOT NULL CHECK(amount >= 0 AND amount <= 999999999),
  date TEXT NOT NULL,
  remark TEXT DEFAULT '',
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
, member_id INTEGER REFERENCES members(id));
CREATE TABLE members (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name TEXT NOT NULL UNIQUE,
  wechat_nickname TEXT DEFAULT '',
  color TEXT DEFAULT '#3b82f6',
  is_active INTEGER DEFAULT 1,
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
);
CREATE TABLE users (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  username TEXT NOT NULL UNIQUE,
  password_hash TEXT NOT NULL,           
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
  updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
);
CREATE TABLE api_tokens (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  name TEXT NOT NULL,                     
  token TEXT NOT NULL UNIQUE,             
  last_used_at DATETIME,                  
  is_active INTEGER DEFAULT 1,            
  created_at DATETIME DEFAULT CURRENT_TIMESTAMP
);
DELETE FROM sqlite_sequence;
CREATE INDEX idx_records_date ON records(date);
CREATE INDEX idx_records_type ON records(type);
CREATE INDEX idx_records_category ON records(category);
CREATE INDEX idx_members_is_active ON members(is_active);
CREATE INDEX idx_members_name ON members(name);
CREATE INDEX idx_records_member_id ON records(member_id);
CREATE INDEX idx_api_tokens_token ON api_tokens(token);
CREATE INDEX idx_api_tokens_is_active ON api_tokens(is_active);